// Initialize default data structure
let data = {
  personal: {
    name: null,
    phone: null,
    email: null
  },
  property: {
    type: null,
    locations: null,
    budget: null,
    timeline: null,
    citizenship: null
  }
};

// Get input from webhook
const input = $input.first();
if (!input || !input.json) {
  return [{
    json: {
      sessionId: 'unknown',
      personal: data.personal,
      property: data.property,
      ready: false
    }
  }];
}

const sessionId = input.json.sessionId || 'unknown';
const rawText = input.json.output || '';

// Attempt to parse JSON from text
try {
  const jsonMatch = rawText.match(/{[\s\S]*}/);
  const jsonString = jsonMatch ? jsonMatch[0] : '{}';
  const parsedData = JSON.parse(jsonString);

  // Personal info
  if (parsedData.personal_details) {
    data.personal.name = parsedData.personal_details.name || null;
    data.personal.phone = parsedData.personal_details.phone || null;
    data.personal.email = parsedData.personal_details.email || null;
  }

  // Property info
  if (parsedData.properties) {
    data.property.type = parsedData.properties.type || null;
    data.property.timeline = parsedData.properties.timeline || null;
    data.property.citizenship = parsedData.properties.citizenship || null;

    // Clean "locations" value to extract just the MRT name
    if (parsedData.properties.locations) {
      data.property.locations = parsedData.properties.locations
        .replace(/(near|beside|around|next to|opposite|close to)\s+/gi, '')
        .trim();
    }

    // Robust budget parsing (handles SGD 2,500 / $2500 / 2500 SGD etc.)
    if (parsedData.properties.budget) {
      const rawBudget = parsedData.properties.budget;
      const cleanedBudget = rawBudget.replace(/[^\d]/g, '');
      data.property.budget = cleanedBudget ? parseInt(cleanedBudget, 10) : null;
    }
  }
} catch (e) {
  console.error('Error parsing JSON data:', e.message);
}

// Check readiness
const ready = Boolean(data.personal.name && data.personal.phone && data.personal.email);

// Final output
return [{
  json: {
    sessionId,
    personal: data.personal,
    property: data.property,
    ready
  }
}];

